import { Awareness } from 'y-protocols/awareness';

type UserSnapshot = ReturnType<Awareness["getLocalState"]>;
declare function useSelf(awareness: Awareness): UserSnapshot;
declare function useSelf<Selection>(awareness: Awareness, selector: (state: UserSnapshot) => Selection, compare?: (a: Selection, b: Selection) => boolean): Selection;

type UsersSnapshot = ReturnType<Awareness["getStates"]>;
declare function useUsers(awareness: Awareness): UsersSnapshot;
declare function useUsers<Selection>(awareness: Awareness, selector: (state: UsersSnapshot) => Selection, compare?: (a: Selection, b: Selection) => boolean): Selection;

export { useSelf, useUsers };
